'use strict';

const
    gulp = require('gulp'),
    rename = require('gulp-rename'),
    uglify = require('gulp-uglify'),
    paths = require('./paths');

function jsMin() {
    return gulp
        .src(`${paths.dest}/*.js`)
        .pipe(uglify())
        .pipe(rename((path) => {
            path.basename += '.min';
        }))
        .pipe(gulp.dest(paths.dest));
}

module.exports = jsMin;
